package main

import "fmt"

/**
 * 二分查找
 * 如果目标值存在返回下标，否则返回 -1
 * @param nums int整型一维数组
 * @param target int整型
 * @return int整型
 */
func search(nums []int, target int) int {
	// write code here

	n := len(nums)
	if n <= 0 || nums[0] > target || (n > 1 && nums[n-1] < target) {
		return -1
	}

	left, right := 0, n-1
	for left < right {
		center := left + (right-left)/2
		if nums[center] < target {
			left = center + 1
		} else if nums[center] > target {
			right = center - 1
		} else {
			right = center
		}
	}

	if nums[left] == target {
		return left
	}

	return -1
}

